SELECT COUNT(*) FROM vacancies;
SELECT work_location, MAX(salary_to) AS max_salary FROM vacancies WHERE work_location IN ('Москва', 'Омск', 'Екатеринбург') AND date_added >= '2022-10-01' AND date_added < '2022-11-01' GROUP BY work_location;

SELECT COUNT(*)  FROM vacancies  WHERE name = 'Java разработчик';
SELECT COUNT(*) FROM vacancies WHERE name = 'Python разработчик' AND work_location IN ('Петербург', 'Санкт-Петербург');
SELECT *  FROM vacancies  WHERE name = 'C++ разработчик' AND salary_to > 200000;
SELECT * FROM vacancies WHERE name = 'Java разработчик' AND salary_from > 150000 AND schedule_type = 'remote';
SELECT COUNT(*)  FROM vacancies  WHERE salary_currency = 'RUR';
SELECT work_location, COUNT(*) as count_vacancies FROM vacancies WHERE name LIKE '%Python%' GROUP BY work_location ORDER BY count_vacancies DESC LIMIT 1;
SELECT COUNT(*)  FROM vacancies  WHERE salary_from IS NULL AND salary_to IS NULL;
SELECT vacancies.salary_currency, MAX(vacancies.salary_to) FROM vacancies WHERE vacancies.salary_to IS NOT NULL GROUP BY vacancies.salary_currency;
SELECT vacancies.vacancy_id, vacancies.name FROM vacancies JOIN companies ON vacancies.company_id = companies.company_id WHERE companies.company_city = 'Омск' AND vacancies.schedule_type = 'mixed';
SELECT DISTINCT direction FROM vacancies;
SELECT AVG(salary_from) AS average_salary_from FROM vacancies WHERE work_location = 'Санкт-Петербург';
SELECT MAX(salary_to) AS max_salary FROM vacancies WHERE work_location = 'Москва' AND salary_to IS NOT NULL;
