SELECT *  FROM vacancies  WHERE date_added = '2020-04-01';
SELECT COUNT(*)  FROM vacancies  WHERE date_added > '2021-01-01';
SELECT vacancy_id, name, salary_from, salary_to, salary_currency FROM vacancies WHERE salary_from >= 50000 AND salary_to <= 100000 AND salary_currency = 'RUR';
